import { getRealPath } from '@vuepress/helper';
import { PLUGIN_NAME } from '../utils.js';
export const prepareClientConfigFile = async (app, { figure, mark }) => {
    let content = '';
    if (figure) {
        content += `\
import "${getRealPath(`${PLUGIN_NAME}/client/styles/figure.css`, import.meta.url)}"
`;
    }
    if (mark) {
        content += `\
import "${getRealPath(`${PLUGIN_NAME}/client/styles/mark.css`, import.meta.url)}"
`;
    }
    return app.writeTemp('markdown-image/client.js', `\
${content}
`);
};
